const button=document.getElementById("button"),count=document.getElementById("count"),popup=document.getElementById("popup"),reset=document.getElementById("reset");let counter=1;function clickCounter(){localStorage.clickcount?localStorage.clickcount=Number(localStorage.clickcount)+1:localStorage.clickcount=1,count.innerText=` ${localStorage.clickcount} times`}const isBigger=()=>{5<=localStorage.clickcount?(reset.classList.remove("reset-closed"),reset.classList.add("reset"),console.log(reset)):(reset.classList.remove("reset"),reset.classList.add("reset-closed"))},handleClick=e=>{e.preventDefault(),popup.classList.add("popup"),clickCounter(),isBigger()},handleClose=e=>{e.target.closest(".alert")||popup.classList.remove("popup")},handleReset=e=>{e.preventDefault(),popup.classList.remove("popup"),popup.classList.add("popup-closed"),localStorage.clear()};button.addEventListener("click",handleClick),popup.addEventListener("click",handleClose),reset.addEventListener("click",handleReset);